#!/bin/sh
set -e

#--------------------------------------------------------------------------------------------------
# Settings
#--------------------------------------------------------------------------------------------------

root="$PWD"

ffmpeg="$PWD/../../../../../Sky/script/ffmpeg"

input="$PWD"

output="$PWD/upscale"

#--------------------------------------------------------------------------------------------------
# Functions
#--------------------------------------------------------------------------------------------------

run()
{
    if [ $# = 3 ]; then

        sh topaz.sh "$input/$1" "$output/$1" $2 $3
    else
        sh topaz.sh "$input/$1" "$output/$1" $2
    fi
}

#--------------------------------------------------------------------------------------------------
# Run
#--------------------------------------------------------------------------------------------------

cd "$ffmpeg"

run "base.mp4" iris-3
run "1.mp4"    iris-3
run "2.mp4"    iris-3
run "3.mp4"    iris-3
run "4.mp4"    iris-3
run "5.mp4"    iris-3
run "6.mp4"    iris-3
run "7.mp4"    iris-3
run "8.mp4"    iris-3
run "9.mp4"    iris-3
run "10.mp4"   iris-3
run "11.mp4"   iris-3
